import React from 'react';
import { JsxElement } from 'typescript';
import {getAPIResource, getAllHeroes, getHeroeByID, getAllComicsOfHero} from '../APIMethods'

class ComicsOfHero extends React.Component {
    constructor(props:any) {
        super(props) 
        this.state = {
            comics: []
        } 
    }

    componentDidMount() {
        getAllComicsOfHero(this.props.id).then((res) => {
            this.setState({heroes: res.data.data.results})
        })
        
    }
    render() {
        const {comics} = this.state;

        return (
                <div>
                    <h1> 
                        Hello superheroes!
                    </h1>
                    <div>
                    {
                    comics.length ? comics.map((hero:any) => (
                        <div> 
                    <div key={hero.id}> {hero.name} has ID: {hero.id} </div>
                    </div>
                    )
                    ) :
                    null
                    }
                    </div>
                </div>)
    }
}

export default ComicsOfHero;
