import React from 'react';
import { JsxElement } from 'typescript';
import axios from 'axios';
import HeroDetails from './HeroDetails';



class Heroes extends React.Component<any, any> {
    constructor(props:any) {
        super(props) 
        this.state = {
            heroes: []
        } 
        this.handleClick = this.handleClick.bind(this)
    }

    handleClick(){
        
    }

    componentDidMount(){
        axios.get('https://gateway.marvel.com/v1/public/characters?ts=1&apikey=e9e00c31be948d38a580d6622122260c&hash=b3168c60491bb1f9138a3506eb6ea2c0')
            .then(res => {
                console.log(res);
                this.setState({heroes: res.data.data.results})
            })
            .catch(function (error) {
                console.log(error);
            })
            
    }
    render() {
        const {heroes} = this.state;

        return (
                <div>
                    <h1> 
                        Hello superheroes!
                    </h1>
                    <div>
                    {
                    heroes.length ? heroes.map((hero:any) => (
                        <div> 
                    <div key={hero.id}> {hero.name} has ID: {hero.id} </div>
                    <button onClick={this.handleClick} > See more details </button>
                    </div>
                    )
                    ) :
                    null
                    }
                    </div>
                </div>)
    }
}

export default Heroes;
