import axios from 'axios';

const APIHostname = 'https://gateway.marvel.com/v1/public/';
const APIKey = 'e9e00c31be948d38a580d6622122260c';
const APIHash = 'b3168c60491bb1f9138a3506eb6ea2c0';


export async function getAPIResourceWithLimit (path: string, limit='limit=5' ) {
    const res = await axios.get(`${APIHostname}${path}?${limit}&ts=1&apikey=${APIKey}&hash=${APIHash}`)
    try {
        return res;
    } catch (error) {
        throw new Error('Error');
    }
}

