import React from 'react';
import { JsxElement } from 'typescript';
import {getAPIResource, getAllHeroes, getHeroeByID, getAllComicsOfHero} from '../../api/APIMethods'
import ComicsOfHero from './ComicsOfHero';
import { NavLink } from 'react-router-dom';





class Heroes extends React.Component<any, any> {
    constructor(props:any) {
        super(props) 
        this.state = {
            heroes: [],
            value: "",
        } 
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(){

    }
    
    handleSubmit(){
    console.log("submit")
    }

    componentDidMount() {
        getAllHeroes().then((res) => {
            this.setState({heroes: res.data.data.results})
        })
        
    }
    render() {
        const {heroes} = this.state;
        console.log(this.state.heroes)
        return (
                <div>
                    <form onSubmit={this.handleSubmit}>    
                        <input type="text" name="query" onChange={this.handleChange} />
                        <button type='submit'> Search </button>
                    </form>

                    <h1> 
                        Hello superheroes!
                    </h1>
                    <div>
                    {
                    heroes.length ? heroes.map((hero:any) => (
                        <div> 
                    <div key={hero.id}> {hero.name} has ID: {hero.id} </div>
                    <div>
                <NavLink to={`/comics/${hero.id}`}>
                     See more 
                </NavLink>
            </div>)
                   
                    </div>
                    )
                    ) :
                    null
                    }
                    </div>
                </div>)
    }
}

export default Heroes;
