import React from 'react';
import { JsxElement } from 'typescript';
import {getAPIResource, getAllHeroes, getHeroeByID, getAllComicsOfHero} from '../APIMethods'

class ComicsOfHero extends React.Component {
    constructor(props:any) {
        super(props)
        this.state = {
            comics: []
        } 
    }

    componentDidMount() {
        getAllComicsOfHero(1011334).then((res) => {
            this.setState({comics: res.data.data.results})
        })
    }
        
    render() {
        const {comics}: any = this.state;
        console.log(this.state);

        return (
                <div>
                    <h1> 
                        Welcome comics!
                    </h1>
                    <div>
                        {
                        comics.length ? comics.map((comic:any) => (
                        <div> 
                            <div key={comic.id}>  
                                <img src={`${comic.thumbnail.path}`+ ".jpg" } /> <br/>
                                {comic.title} <br/>
                                {comic.description} <br/>
                                
                                <br/>
                            </div>
                        </div>
                        )
                        ) : null
                        }
                    </div>
                </ div>)
    }
}

export default ComicsOfHero;
